package assignment1;

public class Swap {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
      int a=10;
      int b=20;
      int temp=a;
      
      System.out.println("Before swaping a and b="+a+" "+b);
      a=b;
      b=temp;
      System.out.println("After swaping a and b="+a+" "+b);
	
	}
	}


